    val listData: ArrayList<ClubDetails>
        get() {
            val list = arrayListOf<ClubDetails>()
            for (position in clubNames.indices) {
                val data = ClubDetails()
                data.id = itemId[position]
                data.name = clubNames[position]
                data.desc = clubDescs[position]
                data.logo = clubLogos[position]
                data.stad_name = stadNames[position]
                data.stad_desc = stadDescs[position]
                data.stad_loc = stadLocs[position]
                data.stad_geo = stadGeos[position]
                data.stad_img = stadImgs[position]
                data.stat_pre = isPressed[position]
                list.add(data)
            }
            return list
        }
